// For defining hboost::parameter keywords that can be inherited from to
// get a nested, class-scoped keyword with the requested alias
#define HBOOST_PARAMETER_NESTED_KEYWORD(tag_namespace, name, alias)                                  \
    namespace tag_namespace                                                                         \
    {                                                                                               \
        template<int Dummy = 0>                                                                     \
        struct name ## _                                                                            \
        {                                                                                           \
            static char const* keyword_name()                                                       \
            {                                                                                       \
                return #name;                                                                       \
            }                                                                                       \
            static ::hboost::parameter::keyword<name ## _<Dummy> > &alias;                           \
        };                                                                                          \
        template<int Dummy>                                                                         \
        ::hboost::parameter::keyword<name ## _<Dummy> > &name ## _<Dummy>::alias =                   \
        ::hboost::parameter::keyword<name ## _<Dummy> >::get();                                      \
        typedef name ## _ <> name;                                                                  \
    }                                                                                               \
    namespace                                                                                       \
    {                                                                                               \
        ::hboost::parameter::keyword<tag_namespace::name> &name =                                    \
        ::hboost::parameter::keyword<tag_namespace::name>::get();                                    \
    }

#endif
